# Table `role`
CREATE TABLE IF NOT EXISTS `role` (
  `id`      INT         NOT NULL AUTO_INCREMENT,
  `name_ru` VARCHAR(45) NOT NULL,
  `name_en` VARCHAR(45) NOT NULL,
  `deleted`     TINYINT(1)     NOT NULL DEFAULT '0',
  `name` VARCHAR(45) NOT NULL,
    PRIMARY KEY (`id`),
  UNIQUE INDEX `name_UNIQUE` (`name_ru` ASC),
  UNIQUE INDEX `name_en_UNIQUE` (`name_en` ASC)
)
  ENGINE = InnoDB;

# Table 'user'
CREATE TABLE `user` (
  `id`          INT(11)        NOT NULL AUTO_INCREMENT,
  `email`       VARCHAR(80)    NOT NULL,
  `password`    VARCHAR(45)    NOT NULL,
  `first_name`   VARCHAR(45)             DEFAULT NULL,
  `last_name`    VARCHAR(45)             DEFAULT NULL,
  `address_id`  INT(11)        NOT NULL,
  `phone_number` VARCHAR(45)             DEFAULT NULL,
  `role_id`     INT(11)        NOT NULL DEFAULT '1',
  `cash`        DECIMAL(12, 0) NOT NULL DEFAULT '0',
  `gender`      VARCHAR(11)    NOT NULL,
  `deleted`     TINYINT(1)     NOT NULL DEFAULT '0',

  PRIMARY KEY (`id`),
  UNIQUE KEY `email_UNIQUE` (`email`)
)
  ENGINE = InnoDB;

# Table `address`
CREATE TABLE IF NOT EXISTS `address` (
  `id`               INT         NOT NULL AUTO_INCREMENT,
  `country`          VARCHAR(45) ,
  `city`             VARCHAR(45) ,
  `street`           VARCHAR(45) ,
  `building_number`  VARCHAR(45) ,
  `apartment_number` VARCHAR(45) ,
  `post_index`       VARCHAR(45) ,
  `deleted`          TINYINT(1)  NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
)
  ENGINE = InnoDB;

# Table `product_type`
CREATE TABLE IF NOT EXISTS `product_type` (
  `id`      INT          NOT NULL AUTO_INCREMENT,
  `name_ru` VARCHAR(255) NOT NULL,
  `name_en` VARCHAR(255) NOT NULL,
  `deleted` TINYINT(1)   NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
)
  ENGINE = InnoDB;

# Table `order`
CREATE TABLE IF NOT EXISTS `order` (
  `id`          INT          NOT NULL AUTO_INCREMENT,
  `user_id`     INT          NOT NULL,
  `created`     DATETIME     NOT NULL,
  `description` VARCHAR(255) NULL,
  `status_id`   INT          NOT NULL,
  `deleted`     TINYINT(1)   NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`, `user_id`)
)
  ENGINE = InnoDB;

# Table `order_status`
CREATE TABLE IF NOT EXISTS `order_status` (
  `id`      INT         NOT NULL AUTO_INCREMENT,
  `name_ru` VARCHAR(45) NOT NULL,
  `name_en` VARCHAR(45) NOT NULL,
  `deleted` TINYINT(1)  NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`),
  UNIQUE INDEX `name_UNIQUE` (`name_ru` ASC),
  UNIQUE INDEX `name_en_UNIQUE` (`name_en` ASC)
)
  ENGINE = InnoDB;

# Table `product`
CREATE TABLE IF NOT EXISTS `product` (
  `id`             INT          NOT NULL AUTO_INCREMENT,
  `name`           TEXT  NOT NULL,
  `price`          DECIMAL(12)  NOT NULL,
  `type_id`        INT          NOT NULL,
  `description_RU` TEXT         NULL,
  `description_EN` TEXT         NULL,
  `deleted`        TINYINT(1)   NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
)
  ENGINE = InnoDB;

# Table `ordering_item`
CREATE TABLE IF NOT EXISTS `ordering_item` (
  `id`         INT        NOT NULL AUTO_INCREMENT,
  `order_id`   INT        NOT NULL,
  `product_id` INT        NOT NULL,
  `amount`     INT        NOT NULL,
  `deleted`    TINYINT(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)


)
  ENGINE = InnoDB;

# Table `storage`
CREATE TABLE IF NOT EXISTS `storage` (
  `id`             INT          NOT NULL AUTO_INCREMENT,
  `name`           VARCHAR(45)  NOT NULL,
  `description_RU` VARCHAR(255) NULL,
  `description_EN` VARCHAR(255) NULL,
  `deleted`        TINYINT(1)   NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`)
)
  ENGINE = InnoDB;

# Table `storage_item`
CREATE TABLE IF NOT EXISTS `storage_item` (
  `id`         INT        NOT NULL AUTO_INCREMENT,
  `storage_id` INT        NOT NULL,
  `product_id` INT        NOT NULL,
  `amount`     INT        NOT NULL,
  `deleted`    TINYINT(1) NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`, `storage_id`, `product_id`)
)
  ENGINE = InnoDB;

# Table `image`
CREATE TABLE IF NOT EXISTS `image` (
  `id`            INT         NOT NULL AUTO_INCREMENT,
  `name`          VARCHAR(45) NOT NULL,
  `product_id`    INT         NOT NULL,
  `path`          LONGBLOB,
  `time_modified` DATETIME    NOT NULL DEFAULT NOW(),
  `deleted`       TINYINT(1)  NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`, `product_id`)
)
  ENGINE = InnoDB;

